package com.jason.week7lab

import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.activity.enableEdgeToEdge
import androidx.compose.runtime.*
import androidx.lifecycle.viewmodel.compose.viewModel
import com.jason.week7lab.ui.theme.Week7LabTheme
import com.jason.week7lab.uiview.ApiKeyConfigScreen
import com.jason.week7lab.uiview.Soal1View
import com.jason.week7lab.viewmodel.WeatherViewModel

class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContent {
            Week7LabTheme {
                WeatherApp()
            }
        }
    }
}

@Composable
fun WeatherApp() {
    val viewModel: WeatherViewModel = viewModel()
    val apiKey by viewModel.apiKey.collectAsState()
    var showConfigScreen by remember { mutableStateOf(true) }

    // Show config screen if no API key is set
    LaunchedEffect(apiKey) {
        if (apiKey.isNotEmpty()) {
            showConfigScreen = false
        }
    }

    if (showConfigScreen) {
        ApiKeyConfigScreen(
            viewModel = viewModel,
            onApiKeySet = {
                showConfigScreen = false
            }
        )
    } else {
        Soal1View(viewModel = viewModel)
    }
}

package com.jason.week7lab.repository

import com.jason.week7lab.model.WeatherResponse
import com.jason.week7lab.service.WeatherApiService
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory

class WeatherRepository {
    private val BASE_URL = "https://api.openweathermap.org/data/2.5/"

    private val loggingInterceptor = HttpLoggingInterceptor().apply {
        level = HttpLoggingInterceptor.Level.BODY
    }

    private val client = OkHttpClient.Builder()
        .addInterceptor(loggingInterceptor)
        .build()

    private val retrofit = Retrofit.Builder()
        .baseUrl(BASE_URL)
        .client(client)
        .addConverterFactory(GsonConverterFactory.create())
        .build()

    private val apiService = retrofit.create(WeatherApiService::class.java)

    suspend fun getWeather(cityName: String, apiKey: String): Result<WeatherResponse> {
        return try {
            val response = apiService.getCurrentWeather(cityName, apiKey)
            Result.success(response)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }
}

package com.jason.week7lab.service

import com.jason.week7lab.model.WeatherResponse
import retrofit2.http.GET
import retrofit2.http.Query

interface WeatherApiService {
    @GET("weather")
    suspend fun getCurrentWeather(
        @Query("q") cityName: String,
        @Query("appid") apiKey: String,
        @Query("units") units: String = "metric"
    ): WeatherResponse
}

