@Composable
fun WeatherContent(weather: WeatherResponse) {
    Column(
        modifier = Modifier
            .fillMaxWidth()
            .padding(16.dp),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        // Location Pin Icon and City Name
        Row(
            verticalAlignment = Alignment.CenterVertically,
            modifier = Modifier.padding(top = 16.dp)
        ) {
            Text(
                text = " ${weather.name}",
                fontSize = 16.sp,
                color = Color.White,
                fontWeight = FontWeight.Medium
            )
        }

        // Date
        Text(
            text = formatDate(weather.dt),
            fontSize = 24.sp,
            fontWeight = FontWeight.Bold,
            color = Color.White,
            modifier = Modifier.padding(top = 8.dp)
        )

        Text(
            text = "Updated as of ${formatTime(weather.dt)}",
            fontSize = 12.sp,
            color = Color.White.copy(alpha = 0.7f),
            modifier = Modifier.padding(top = 4.dp)
        )

        Spacer(modifier = Modifier.height(32.dp))

        // Weather Icon and Main Info
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.Center,
            verticalAlignment = Alignment.CenterVertically
        ) {
            // Weather Icon from API
            if (weather.weather.isNotEmpty()) {
                val iconCode = weather.weather[0].icon
                val iconUrl = "https://openweathermap.org/img/wn/${iconCode}.png"

                Image(
                    painter = rememberAsyncImagePainter(iconUrl),
                    contentDescription = "Weather Icon",
                    modifier = Modifier.size(80.dp),
                    contentScale = ContentScale.Fit
                )
            }

            Spacer(modifier = Modifier.width(16.dp))

            // Panda character placeholder
            Text(
                text = getWeatherEmoji(weather.weather.firstOrNull()?.main ?: "Clear"),
                fontSize = 100.sp
            )
        }

        Spacer(modifier = Modifier.height(16.dp))

        // Weather Description
        if (weather.weather.isNotEmpty()) {
            Text(
                text = weather.weather[0].main,
                fontSize = 24.sp,
                fontWeight = FontWeight.SemiBold,
                color = Color.White
            )
        }

        // Temperature
        Text(
            text = "${weather.main.temp.toInt()}°C",
            fontSize = 72.sp,
            fontWeight = FontWeight.Bold,
            color = Color.White
        )

        Spacer(modifier = Modifier.height(32.dp))

        // Weather Details Grid
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceEvenly
        ) {
            WeatherDetailCard(
                icon = "",
                label = "HUMIDITY",
                value = "${weather.main.humidity}%"
            )

            WeatherDetailCard(
                icon = "",
                label = "WIND",
                value = "${weather.wind.speed.toInt()} km/h"
            )

            WeatherDetailCard(
                icon = "️",
                label = "FEELS LIKE",
                value = "${weather.main.feels_like.toInt()}°"
            )
        }

        Spacer(modifier = Modifier.height(16.dp))

        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceEvenly
        ) {
            WeatherDetailCard(
                icon = "️",
                label = "RAINFALL",
                value = "0.0 mm"
            )

            WeatherDetailCard(
                icon = "",
                label = "PRESSURE",
                value = "${weather.main.pressure} hPa"
            )

            WeatherDetailCard(
                icon = "☁️",
                label = "CLOUDS",
                value = "${weather.clouds.all}%"
            )
        }

        Spacer(modifier = Modifier.height(16.dp))

        // Sunrise and Sunset
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceEvenly
        ) {
            WeatherDetailCard(
                icon = "",
                label = "SUNRISE",
                value = formatTime(weather.sys.sunrise)
            )

            WeatherDetailCard(
                icon = "",
                label = "SUNSET",
                value = formatTime(weather.sys.sunset)
            )
        }

        Spacer(modifier = Modifier.height(32.dp))
    }
}

@Composable
fun WeatherDetailCard(icon: String, label: String, value: String) {
    Card(
        modifier = Modifier
            .width(110.dp)
            .height(100.dp),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = Color.White.copy(alpha = 0.15f)
        )
    ) {
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(12.dp),
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.SpaceEvenly
        ) {
            Text(
                text = icon,
                fontSize = 28.sp
            )
            Text(
                text = label,
                fontSize = 10.sp,
                color = Color.White.copy(alpha = 0.7f),
                fontWeight = FontWeight.Medium
            )
            Text(
                text = value,
                fontSize = 16.sp,
                color = Color.White,
                fontWeight = FontWeight.Bold
            )
        }
    }
}

fun getWeatherEmoji(weatherMain: String): String {
    return when (weatherMain.lowercase()) {
        "clear" -> ""
        "clouds" -> ""
        "rain" -> ""
        "drizzle" -> ""
        "thunderstorm" -> ""
        "snow" -> ""
        else -> ""
    }
}

fun formatDate(timestamp: Long): String {
    val sdf = SimpleDateFormat("MMMM dd", Locale.ENGLISH)
    return sdf.format(Date(timestamp * 1000))
}

fun formatTime(timestamp: Long): String {
    val sdf = SimpleDateFormat("h:mm a", Locale.ENGLISH)
    return sdf.format(Date(timestamp * 1000))
}

package com.jason.week7lab.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.jason.week7lab.model.WeatherResponse
import com.jason.week7lab.repository.WeatherRepository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch

sealed class WeatherUiState {
    object Initial : WeatherUiState()
    object Loading : WeatherUiState()
    data class Success(val weather: WeatherResponse) : WeatherUiState()
    data class Error(val message: String) : WeatherUiState()
}

class WeatherViewModel : ViewModel() {
    private val repository = WeatherRepository()

    private val _uiState = MutableStateFlow<WeatherUiState>(WeatherUiState.Initial)
    val uiState: StateFlow<WeatherUiState> = _uiState.asStateFlow()

    private val _apiKey = MutableStateFlow("")
    val apiKey: StateFlow<String> = _apiKey.asStateFlow()

    fun setApiKey(key: String) {
        _apiKey.value = key
    }

    fun searchWeather(cityName: String) {
        if (_apiKey.value.isEmpty()) {
            _uiState.value = WeatherUiState.Error("Please set API key first")
            return
        }

        if (cityName.isEmpty()) {
            _uiState.value = WeatherUiState.Error("Please enter a city name")
            return
        }

        viewModelScope.launch {
            _uiState.value = WeatherUiState.Loading

            val result = repository.getWeather(cityName, _apiKey.value)

            _uiState.value = if (result.isSuccess) {
                WeatherUiState.Success(result.getOrNull()!!)
            } else {
                val error = result.exceptionOrNull()
                when {
                    error?.message?.contains("404") == true -> 
                        WeatherUiState.Error("HTTP 404 Not Found")
                    error?.message?.contains("401") == true -> 
                        WeatherUiState.Error("Invalid API Key")
                    else -> 
                        WeatherUiState.Error(error?.message ?: "Something went wrong")
                }
            }
        }
    }
}
